import org.apache.commons.lang.RandomStringUtils

// 1. 알파벳 + 숫자 혼합 (10자리)
def alphanumeric = RandomStringUtils.randomAlphanumeric(10)
log.info "알파벳숫자: " + alphanumeric

// 2. 순수 알파벳만 (8자리)
def alphabetic = RandomStringUtils.randomAlphabetic(8)
log.info "알파벳만: " + alphabetic

// 3. 숫자 형태 문자열만 (6자리)
def numeric = RandomStringUtils.randomNumeric(6)
log.info "숫자문자열: " + numeric

// 4. 지정한 문자 범위 내에서 생성 (예: 'abc' 중에서만 5자리)
def custom = RandomStringUtils.random(5, "abcdef")
log.info "커스텀문자: " + custom

