mapper.insert:
		INSERT INTO
		user
		( user_id
		, user_passwd
		, user_auth
		, user_name

		)
		VALUES (
		#{userId}
		, #{userPasswd}
		, #{userAuth}
		, #{userName}
		)

mapper.update:
		UPDATE user
		SET user_passwd = #{userPasswd}
		, user_auth = #{userAuth}
		, user_name = #{userName}
		WHERE user_id = #{userId}

mapper.select:
SELECT user_id as "userId" , user_passwd as "userPasswd" , user_auth as "userAuth" , user_name as "userName" , reg_date as "regDate" FROM user
order by reg_date desc
LIMIT #{startData} , #{pageSize}
